import { useEffect, useState } from "react";

const BookList = ({ books, title, handleBorrowBook }) => {

    const [currentPage, setCurrentPage] = useState(1);
    const booksPerPage = 10;
    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const getVisibleBooks = () => {
        const startIndex = (currentPage - 1) * booksPerPage;
        const endIndex = startIndex + booksPerPage;
        return books.slice(startIndex, endIndex);
      };

    const renderPagination = () => {
        const totalPages = Math.ceil(books.length / booksPerPage);
        const pageNumbers = [];
        for (let i = 1; i <= totalPages; i++) {
          pageNumbers.push(i);
        }

        return (
          <ul className="pagination">
            {pageNumbers.map((pageNumber) => (
              <li key={pageNumber} className={pageNumber === currentPage ? 'active' : ''}>
                <button onClick={() => handlePageChange(pageNumber)}>{pageNumber}</button>
              </li>
            ))}
          </ul>
        );
      };

      const visibleBooks = getVisibleBooks();

    return ( 
        <div className="book-list">
            <h2>{title}</h2>
            {
                books.map((book) => (
                    <div className="book-preview" key={book.id}>
                        <h2>{book.title}</h2>
                        <p>Author: {book.author}</p>
                        <p>Year: { book.date }</p>
                        <a className="borrow" onClick={() => handleBorrowBook(book.id)}>Borrow</a>
                        <span className="book-status">{book.status}</span>
                    </div> 
                ))
            }
        </div>
     );
}

export default BookList;
