import { useState } from "react";

const Home = () => {
    const [books, setBooks] = useState([
        {
            "id": 1,
            "title": "Oedipus at Colonus",
            "author": "Jebb, Richard Claverhouse, Sir, 1841-1905",
            "date": "1930",
            "status": "free"
        },
        {
            "id": 2,
            "title": "The Iliad",
            "author": "Bryant, William Cullen, 1794-1878",
            "date": "c1870",
            "status": "free"
        },
        {
            "id": 3,
            "title": "The Prometheus",
            "author": "Buckley, Theodore Alois, 1825-1856",
            "date": "1899",
            "status": "free"
        },
        {
            "id": 4,
            "title": "Parmenides",
            "author": "Maguire, Thomas, 1831-1889",
            "date": "1882",
            "status": "free"
        }]);

    return ( 
        <div className="home">
            <h2>Books</h2>
            {
                books.map((book) => (

                        <div className="book-preview" key={book.id}>
                            <h2>{book.title}</h2>
                            <p>Author: {book.author}</p>
                            <p>Year: { book.date }</p>

                        <span className="book-status">{book.status}</span>
                        </div> 

                ))
            }
        </div>
     );
}

export default Home;
