const BookList = ({ books, title, handleBorrowBook }) => {

    return ( 
        <div>
            <h2>{title}</h2>
            {
                books.map((book) => (
                    <div className="book-preview" key={book.id}>
                        <h2>{book.title}</h2>
                        <p>Author: {book.author}</p>
                        <p>Year: { book.date }</p>
                        <a className="borrow" onClick={() => handleBorrowBook(book.id)}>Borrow</a>
                        <span className="book-status">{book.status}</span>
                    </div> 
                ))
            }
        </div>
     );
}

export default BookList;
