import { useState } from 'react';
import bcrypt from 'bcryptjs';
import HttpService from "../services/http";

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const httpService = new HttpService();

    try {
      // Generate salt for password hashing
      const salt = await bcrypt.genSalt(10);

      // Hash the password using bcrypt
      const hashedPassword = await bcrypt.hash(password, salt);

      const response = await httpService.post('/api/v1/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password: hashedPassword }),
      });

      if (!response.status === 200) {
        throw new Error('Login failed');
      }

      // Handle successful login (e.g., redirect to protected area)
      console.log('Login successful!');
      // You can use browser storage (localStorage or sessionStorage) 
      // to store a token or user information here
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(event) => setUsername(event.target.value)}
        />
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
